#!/../bin/shecho "get code"git statusecho "create code coverage badge"rm -r coverage # Python image doesnt seem to have the necessary cli tools mkdir coveragepython3 manage.py makemigrations Fridgify_Backend --settings=Fridgify_Backend.settings.localpython3 manage.py migrate Fridgify_Backend --settings=Fridgify_Backend.settings.localpython3 manage.py test --settings=Fridgify_Backend.settings.localmv .coverage ./coveragecd coveragecoverage-badge -o coverage.svg